import redis
from dataclasses import dataclass
from typing import Any, Callable
import json
import time

@dataclass
class Task:
    id: str
    func: str
    args: tuple
    kwargs: dict

class TaskQueue:
    def __init__(self, redis_url: str):
        self.redis = redis.from_url(redis_url)
        self.functions: dict[str, Callable] = {}

    def register(self, func: Callable):
        self.functions[func.__name__] = func

    def enqueue(self, func: Callable, *args, **kwargs) -> str:
        task = Task(
            id=str(time.time()),
            func=func.__name__,
            args=args,
            kwargs=kwargs
        )
        self.redis.lpush('task_queue', json.dumps(vars(task)))
        return task.id

    def process_tasks(self):
        while True:
            task_data = self.redis.brpop('task_queue')
            if task_data:
                task = Task(**json.loads(task_data[1]))
                func = self.functions.get(task.func)
                if func:
                    try:
                        result = func(*task.args, **task.kwargs)
                        self.redis.hset('results', task.id, json.dumps(result))
                    except Exception as e:
                        self.redis.hset('errors', task.id, str(e))

