use wasm_bindgen::prelude::*;

#[wasm_bindgen]
pub struct ImageProcessor {
    width: usize,
    height: usize,
    data: Vec<u8>,
}

#[wasm_bindgen]
impl ImageProcessor {
    #[wasm_bindgen(constructor)]
    pub fn new(width: usize, height: usize, data: Vec<u8>) -> Self {
        Self { width, height, data }
    }

    pub fn detect_edges(&self) -> Vec<u8> {
        let mut result = vec![0; self.data.len()];
        let sobel_x = [-1, 0, 1, -2, 0, 2, -1, 0, 1];
        let sobel_y = [-1, -2, -1, 0, 0, 0, 1, 2, 1];

        for y in 1..self.height-1 {
            for x in 1..self.width-1 {
                let mut px = 0;
                let mut py = 0;

                for ky in 0..3 {
                    for kx in 0..3 {
                        let idx = ((y+ky-1)*self.width + (x+kx-1)) * 4;
                        let pixel = self.data[idx] as i32;
                        px += pixel * sobel_x[ky*3 + kx];
                        py += pixel * sobel_y[ky*3 + kx];
                    }
                }

                let magnitude = ((px*px + py*py) as f64).sqrt() as u8;
                let idx = (y*self.width + x) * 4;
                result[idx] = magnitude;
                result[idx+1] = magnitude;
                result[idx+2] = magnitude;
                result[idx+3] = 255;
            }
        }
        result
    }
}

